export const stringToInt = (dateArr) => {
    if(dateArr[2] === "Jan") {
        return "01"
    } else if(dateArr[2] === "Feb") {
        return "02"
    } else if(dateArr[2] === "Mar") {
        return "03"
    } else if(dateArr[2] === "Apr") {
        return "04"
    } else if(dateArr[2] === "May") {
        return "05"
    } else if(dateArr[2] === "Jun") {
        return "06"
    } else if(dateArr[2] === "Jul") {
        return "07"
    } else if(dateArr[2] === "Aug") {
        return "08"
    } else if(dateArr[2] === "Sep") {
        return "09"
    } else if(dateArr[2] === "Oct") {
        return "10"
    } else if(dateArr[2] === "Nov") {
        return "11"
    } else {
        return "12"
    }
}

